package HelloJava;

public class DO_While_Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;
		
		do {
			System.out.println(i +" Tahsin is a good boy");
			i++;
		}
		while (i<=10);
		
		System.out.println("END_");
		
		
		
		
		
		
		
		
		
		
		
	}

}
