package HelloJava;

public class VariablesContinued {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("VariablesContinued");
		
		int var=1000;
		
		System.out.println(var+200);
		
		long BigNumber =10000L;
		System.out.println(BigNumber+10000);
		
		short shoort=32000;
		System.out.println(shoort+200);
		
		
		byte bite=120;
		System.out.println(bite+200);
		
		double dooble=100.98d;
		System.out.println(dooble+200);
		
		boolean decision=true;
		System.out.println("MY love is " +decision);
		
		char letter='T';
		System.out.println("my char value is "+letter );
		
		bite=100;
		System.out.println(bite);
		
		
		
		
		
		
		
		
		
		
	}

}
